import './list.js';
import './detail.js';
import './main.html';

Template.submission_main.onCreated(function() {
	this.currentFilter = new ReactiveObjects();
	this.currentSubmissionId = new ReactiveVar();

	this.autorun(() => {
		if (!this.data.currentProblemId || !this.data.currentProblemId.get()) {
			ReactiveObjects.removeProperty(this.currentFilter, 'problemId');
			delete this.currentFilter.problemId;
		} else {
			this.currentFilter.ReactiveFunctions.addProperty('problemId', this.data.currentProblemId.get());
		}
		if (!this.data.currentStudentId || !this.data.currentStudentId.get()) {
			ReactiveObjects.removeProperty(this.currentFilter, 'studentId');
			delete this.currentFilter.studentId;
		} else {
			this.currentFilter.ReactiveFunctions.addProperty('studentId', this.data.currentStudentId.get());
		}
		console.log(this.currentFilter.problemId);
	});
});

Template.submission_main.helpers({
	shareArgs() {
		return {
			currentFilter: Template.instance().currentFilter,
			currentSubmissionId: Template.instance().currentSubmissionId
		};
	}
});
