import './list.js';
import './detail.js';
import './main.html';

Template.submission_main.onCreated(function() {
	this.currentFilter = new ReactiveObjects({ problemId: null, studentId: null });
	this.currentSubmissionId = new ReactiveVar(null);

	this.autorun(() => {
		this.currentFilter.problemId = this.data.currentProblemId ? this.data.currentProblemId.get() : null;
		this.currentFilter.studentId = this.data.currentStudentId ? this.data.currentStudentId.get() : null;
	});
});

Template.submission_main.helpers({
	shareArgs() {
		return {
			currentFilter: Template.instance().currentFilter,
			currentSubmissionId: Template.instance().currentSubmissionId
		};
	}
});
