import { Problems } from '/imports/api/problem.js';
import { Students } from '/imports/api/student.js';
import { Submissions } from '/imports/api/submission.js';
import './list.html';

Template.submission_list.onCreated(function() {
	this.currentFilter = new ReactiveObjects();

	this.autorun(() => {
		if (this.data.currentProblemId && this.data.currentProblemId.get()) {
			currentFilter.ReactiveFunctions.addProperty('problemId', this.data.currentProblemId.get());
		} else {
			ReactiveObjects.removeProperty(this.currentFilter, 'problemId');
			delete this.currentFilter.problemId;
		}
		if (this.data.currentStudentId && this.data.currentStudentId.get()) {
			currentFilter.ReactiveFunctions.addProperty('studentId', this.data.currentStudentId.get());
		} else {
			ReactiveObjects.removeProperty(this.currentFilter, 'studentId');
			delete this.currentFilter.studentId;
		}
		this.subscribe('Submissions.findAllByFilter', this.currentFilter);
	});
});

Template.submission_list.helpers({
	hasFilter() {
		const instance = Template.instance();
		return instance.currentFilter.problemId || instance.currentFilter.studentId;
	},
	submissionList() {
		return Submissions.find(Template.instance().currentFilter, {
			fields: { _id: 1, problemId: 1, studentId: 1, createdAt: 1, status: 1, isExam: 1 },
			sort: { createdAt: -1 }
		});
	},
	getStudentUsername(studentId) {
		return Meteor.getValue(Students, studentId, 'username');
	},
	getProblemTitle(problemId) {
		return Meteor.getValue(Problems, problemId, 'title');
	}
});

Template.submission_list.events({
	'click .remove-btn'(event) {
		const id = event.currentTarget.parentElement.dataset.id;
		Meteor.call('Submissions.remove', { _id: id });
	},
	'click .update-btn'(event) {
		Meteor.call('Submissions.update', id, {});
	},
	'click .get-btn'(event) {
		const id = event.currentTarget.dataset.id;
		this.currentSubmissionId.set(id);
	}
});
