import './list.js';
import './detail.js';
import './main.html';

Template.submission_main.onCreated(function() {
	this.currentFilter = new ReactiveObject({
		problemId: this.data.currentProblemId ? this.data.currentProblemId.get() : null,
		studentId: this.data.currentStudentId ? this.data.currentStudentId.get() : null
	});
	this.currentSubmissionId = new ReactiveVar(null);
});

Template.submission_main.helpers({
	shareArgs() {
		return {
			currentFilter: Template.instance().currentFilter,
			currentSubmissionId: Template.instance().currentSubmissionId
		};
	}
});
