import './list.js';
import './detail.js';
import './main.html';

Template.submission_main.onCreated(function() {
	this.currentFilter = new ReactiveVar({});
	this.currentSubmissionId = new ReactiveVar(null);
	this.autorun(() => {
		const instance = Template.instance();
		if (this.data.currentProblemId) instance.currentFitler.problemId = this.data.currentProblemId.get();
		if (this.data.currentStudentId) instance.currentFilter.studentId = this.data.currentStudentId.get();
	});
});

Template.submission_main.helpers({
	shareArgs() {
		return {
			currentFilter: Template.instance().currentFilter,
			currentSubmissionId: Template.instance().currentSubmissionId
		};
	}
});
