import { Submissions } from '/imports/api/submission.js';
import './list.html';

Template.submission_list.onCreated(function() {
	this.autorun(() => {
		const filter = {};
		if (this.data.currentProblemId && this.data.currentProblemId.get()) filter.problemId = this.data.currentProblemId.get();
		if (this.data.currentStudentId && this.data.currentStudentId.get()) filter.studentId = this.data.currentStudentId.get();
		this.subscribe('Submissions.findAllByFilter', filter);
		console.log(filter);
	});
});

Template.submission_list.helpers({
	hasFilter() {
		return (this.currentProblemId && this.currentProblemId.get()) || (this.currentStudentId && this.currentStudentId.get());
	},
	submissionList() {
		console.log('gggggg');
		return Submissions.find({}, { fields: { _id: 1, problemId: 1, studentId: 1, createdAt: 1, status: 1, isExam: 1 }});
	},
	getStudentUsername(studentId) {
		return Meteor.getValue(Students, studentId, 'username');
	},
	getProblemTitle(problemId) {
		return Meteor.getValue(Problems, problemId, 'title');
	}
});

Template.submission_list.events({
	'click .remove-btn'(event) {
		const id = event.currentTarget.parentElement.dataset.id;
		Meteor.call('Submissions.remove', { _id: id });
	},
	'click .update-btn'(event) {
		Meteor.call('Submissions.update', id, {});
	},
	'click .get-btn'(event) {
		const id = event.currentTarget.dataset.id;
		this.currentSubmissionId.set(id);
	}
});
