import { Problems } from '/imports/api/problem.js';
import { Students } from '/imports/api/student.js';
import { Submissions } from '/imports/api/submission.js';
import './list.html';

function omitNull(obj) {
	const ret = {};

	for (let key in obj._items) {
		if (obj._items[key])
			ret[key] = obj._items[key];
	}
	return ret;
}
Template.submission_list.onCreated(function() {
	this.currentFilter = new ReactiveObject({ problemId: null, studentId: null });

	this.autorun(() => {
		if (this.data.currentProblemId && this.data.currentProblemId.get()) {
			this.currentFilter.problemId = this.data.currentProblemId.get();
		} else {
			this.currentFilter.problemId = null;
		}
		if (this.data.currentStudentId && this.data.currentStudentId.get()) {
			this.currentFilter.studentId = this.data.currentStudentId.get();
		} else {
			this.currentFilter.studentId = null;
		}
		console.log(omitNull(this.currentFilter));
		this.subscribe('Submissions.findAllByFilter', omitNull(this.currentFilter));
	});
});

Template.submission_list.helpers({
	hasFilter() {
		const currentFilter = omitNull(Template.instance().currentFilter);
		return !_.isEmpty(currentFilter);
	},
	submissionList() {
		const currentFilter = omitNull(Template.instance().currentFilter);
		console.log(currentFilter);
		return Submissions.find(currentFilter, { fields: { _id: 1, problemId: 1, studentId: 1, createdAt: 1, status: 1, isExam: 1 }, sort: { createdAt: -1 }});
	},
	getStudentUsername(studentId) {
		return Meteor.getValue(Students, studentId, 'username');
	},
	getProblemTitle(problemId) {
		return Meteor.getValue(Problems, problemId, 'title');
	}
});

Template.submission_list.events({
	'click .remove-btn'(event) {
		const id = event.currentTarget.parentElement.dataset.id;
		Meteor.call('Submissions.remove', { _id: id });
	},
	'click .update-btn'(event) {
		Meteor.call('Submissions.update', id, {});
	},
	'click .get-btn'(event) {
		const id = event.currentTarget.dataset.id;
		this.currentSubmissionId.set(id);
	}
});
