import { Submissions } from '/imports/api/submission.js';
import './list.html';

Template.submission_list.onCreated(function() {
	this.autorun(() => {
		const filter = this.data.currentFilter.get();
		if (filter.problemId) filter.problemId = filter.problemId.get();
		if (filter.studentId) filter.studentId = filter.studentId.get();
		this.subscribe('Submissions.findAllByFilter', filter);
	});
});

Template.submission_list.helpers({
	hasFilter() {
		const currentFilter = this.currentFilter.get();
		return (currentFilter.problemId || currentFilter.studentId);
	},
	submissionList() {
		return Submissions.find(this.currentFilter.get(), { fields: { _id: 1, problemId: 1, studentId: 1, createdAt: 1, status: 1, isExam: 1 }});
	},
	isCurrentSubmission(id) {
		return id == this.currentSubmissionId.get();
	}
});

Template.submission_list.events({
	'click .remove-btn'(event) {
		const id = event.currentTarget.parentElement.dataset.id;
		Meteor.call('Submissions.remove', { _id: id });
	},
	'click .get-btn'(event) {
		const id = event.currentTarget.dataset.id;
		this.currentSubmissionId.set(id);
	}
});
