import { Problems, ProblemTests } from './problem.js';
import './utility.js';

export const Submissions = new Mongo.Collection('Submissions');
export const SubmissionResults = new Mongo.Collection('SubmissionResults');

if (Meteor.isServer) {
	Meteor.publish('Submissions.findAllByFilter', function(problemId, studentId) {
		const filter = { $or: [{ problemId: problemId }, { studentId, studentId }]};
		const option = { fields: { _id: 1, problemId: 1, studentId: 1, createdAt: 1, status: 1, isExam: 1 }};
console.log(filter);
		return Submissions.find(filter, option);
	});
	Meteor.publish('Submissions.findOne', function(id) {
		const filter = { _id: id };
		const option = {};

		if (!Meteor.isAdmin() && !Meteor.isOwner(Meteor.getValue(Submissions, id, 'studentId'))) {
			option.fields = { code: 0, error: 0 };
		}
		return Submissions.find(filter, option);
	});
	Meteor.publish('SubmissionResults.findAllBySubmission', function(submissionId) {
		const filter = { submissionId: submissionId };
		const option = {};

		if (!Meteor.getValue(Submissions, submissionId, 'isExam')) {
			const problemId = Meteor.getValue(Submissions, submissionId, 'problemId');
			filter.problemTestId = { $in: [] };
			
			ProblemTests.find({ problemId: problemId, isPublic: 1 }).forEach((problemTest) => {
				filter.problemTestId.$in.push(problemTest._id);
			});
		}
		return SubmissionResults.find(filter, option);
	});
}

Meteor.methods({
	'Submissions.insert'(problemId, studentId, lang, code) {
		if (!Meteor.isOwner(studentId) || !Meteor.getValue(Problems, problemId, '_id')) return;
		
		const submissionId = Submissions.insert({
			problemId: problemId,
			studentId: studentId,
			createdAt: new Date().getTime(),
			status: 'Pending',
			lang: lang,
			code: code,
			error: null,
			isExam: Meteor.getValue(Problems, problemId, 'isExam')
		});
		if (Meteor.isServer) {
			Meteor.call('Submissions.createDir', submissionId);
		}
	},
	'Submissions.remove'(filter) {
		if (!Meteor.isAdmin()) return;

		Submissions.find(filter).forEach((submission) => {
			Meteor.call('SubmissionResults.remove', { submissionId: submission._id });

			if (Meteor.isServer) {
				Meteor.call('Submissions.removeDir', submission._id);
			}
		});
		Submissions.remove(filter);
	},
	'Submissions.update'(id, option) {
		if (!Meteor.isAdmin()) return;
		
		Submissions.update({ _id: id }, { $set: option });

		if (Meteor.isServer) {
			if (option.lang || option.code)
				Meteor.call('Submissions.createDir', id);
		}
	},
	'SubmissionResults.insert'(problemTestId, submissionId) {
		//if (!Meteor.isAdmin()) return;

		const submissionResultId = SubmissionResults.insert({
			problemTestId: problemTestId,
			submissionId: submissionId,
			timeUsed: null,
			memoryUsed: null,
			status: 'Pending'
		});
		if (Meteor.isServer) {
			Meteor.call('SubmissionResults.createDir', submissionResultId);
		}
	},
	'SubmissionResults.remove'(filter) {
		if (!Meteor.isAdmin()) return;

		SubmissionResults.find(filter).forEach((submissionResult) => {
			if (Meteor.isServer) {
				Meteor.call('SubmissionResults.removeDir', submissionResult._id);
			}
		});
		SubmissionResults.remove(filter);
	},
	'SubmissionResults.update'(id) {
		if (!Meteor.isAdmin()) return;

		if (Meteor.isServer) {
			Meteor.call('SubmissionResults.createDir', id);
		}
	}
});
