import { Problems } from '/imports/api/problem.js';
import { Students } from '/imports/api/student.js';
import './list.js';
import './detail.js';
import './main.html';

Template.submission_main.onCreated(function() {
	const filter = {};
	if (this.data.currentProblemId) filter.problemId = this.data.currentProblemId.get();
	if (this.data.currentStudentId) filter.studentId = this.data.currentStudentId.get();

	this.currentFilter = new ReactiveVar(filter);
	this.currentSubmissionId = new ReactiveVar(null);
});

Template.submission_main.helpers({
	shareArgs() {
		return {
			currentFilter: Template.instance().currentFilter,
			currentSubmissionId: Template.instance().currentSubmissionId
		};
	}
});
